mapper.select:
SELECT gd.name AS district_name, COUNT(DISTINCT cc.name) as chbecd, SUM(CASE WHEN upp.gender_id = 9 THEN 1 END) AS maleCount, SUM(CASE WHEN upp.gender_id = 10 THEN 1 ELSE 0 END) AS female_count, COUNT(*) AS total_count, SUM(CASE WHEN upp.gender_id = 9 THEN si.amountpayroll ELSE 0 END) AS total_paid_by_male, SUM(CASE WHEN upp.gender_id = 10 THEN si.amountpayroll ELSE 0 END) AS total_paid_by_female, SUM(si.amountpayroll) AS total_paid_by_all, COALESCE(MAX(child_counts.total_children), 0) AS total_children, COALESCE(MAX(child_counts.total_boys_children), 0) AS total_boys_children, COALESCE(MAX(child_counts.total_girls_children), 0) AS total_girls_children
from SpPayrollEcd s JOIN sppayrollecditem si ON s.id = si.payroll_id JOIN vupecdworker w ON w.id = si.worker_id JOIN vupecdcenter cc on cc.id = w.center_id JOIN censusdataperson c on c.id =w.cdp_id JOIN ubuperson upp on upp.id = c.person_id JOIN govsector g ON s.sector_id = g.id JOIN govdistrict gd on gd.id = g.district_id LEFT JOIN ( SELECT gd.id AS district_id, COUNT(DISTINCT child.id) AS total_children, COUNT(DISTINCT CASE WHEN upp.gender_id = 9 THEN child.id END) AS total_boys_children, COUNT(DISTINCT CASE WHEN upp.gender_id = 10 THEN child.id END) AS total_girls_children FROM vupecdcenter center JOIN govcell cell ON center.cell_id = cell.id JOIN govsector gs ON cell.sector_id = gs.id JOIN govdistrict gd ON gs.district_id = gd.id JOIN vupecdmonitoring monitoring ON center.id = monitoring.center_id JOIN vupecdmonitoringchild child ON monitoring.id = child.monitoring_id JOIN censusdataperson c ON child.cdp_id = c.id JOIN ubuperson upp ON upp.id = c.person_id WHERE monitoring.month_id = '1421'
AND monitoring.year_id = #{fiscYr}
AND gd.id = #{districtNo} /* BUGESERA */
GROUP BY gd.id ) AS child_counts ON child_counts.district_id = gd.id
where
s.id ='3976'
AND gd.id = #{districtNo}
AND w.year_id =#{fiscYr}
GROUP BY gd.name order by gd.name

SELECT COUNT(DISTINCT sector.name) AS numberOfSectorsCovered, COUNT( item.household_id) AS numberOfEligibleHousehold, COUNT( CASE WHEN up.gender_id = 9 THEN item.household_id END) AS maleCount, COUNT(CASE WHEN up.gender_id = 10 THEN item.household_id END) AS femaleCount, SUM(item.amount) AS totalPaidByAll, SUM( CASE WHEN up.gender_id = 9 THEN item.amount END) AS totalPaidByMale, SUM( CASE WHEN up.gender_id = 10 THEN item.amount END) AS totalPaidByFemale FROM VupDsPayroll payroll JOIN VupDsPayrollItem item ON payroll.id = item.payroll_id JOIN censusdatahousehold cd ON cd.id = item.cdh_id JOIN ubuperson up ON cd.head_id = up.id JOIN GovSector sector ON sector.id = payroll.sector_id JOIN govdistrict gd on gd.id = sector.district_id WHERE 1=1
AND gd.id = #{districtNo} /* 30 BUGESERA */
AND payroll.year_id = #{fiscYr}
